import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

import { host } from './host';

@Injectable({
  providedIn: 'root'
})
export class CurrDataService {

  constructor(
    private httpClient: HttpClient,
  ){ }

  async takeData(season: string, group: string, bodyPart: string = null)
  {
    const settings = [
      {
        Men: "Mężczyźni",
        Women: "Kobiety",
        Child: "Dzieci",
      },
      {
        spring: "Wiosna",
        summer: "Lato",
        fall: "Jesień",
        winter: "Zima"
      },
      {
        head: "Głowa",
        corpus: "Korpus",
        legs: "Nogi",
        feet: "Stopy"
      }
    ];

    function values(data)
    {
        let res = null;
        settings.filter((e) => {

          for(let x in e)
          {
            if(e[x] === data) res = x;
          }
        })

      return res;
    }

    const thatSeason = values(season);
    const thatGroup = values(group);

    const url = `${host}/stuff/sezon/${thatSeason}/${thatGroup}`;
    bodyPart === null? null: [values(bodyPart), url+`/${bodyPart}`];

    return this.httpClient.get(url);
  }
}
