import { Injectable } from "@angular/core";
import { ActivatedRouteSnapshot, CanActivate, RouterStateSnapshot } from "@angular/router";

import { GetProductsService } from "../../basket.service";

@Injectable()
export class PaymantGuard implements CanActivate
{

    constructor(private basketService: GetProductsService){}

    async canActivate(route: ActivatedRouteSnapshot, state: RouterStateSnapshot): Promise<boolean> {
        
        const that = this;
        function getData()
        {
            return new Promise((resolve, reject) => {
                that.basketService.getProducts().subscribe(e => console.log(e))
            });
        };

        await getData();
        return true;
    }
}
