import { Injectable } from "@angular/core";
import { ActivatedRouteSnapshot, CanActivate, Router, RouterStateSnapshot } from "@angular/router";
import { take } from "rxjs/operators";

import { GetProductsService } from "../../basket.service";

@Injectable()
export class PaymantGuard implements CanActivate
{

    constructor(
        private basketService: GetProductsService,
        private router: Router
        ){}

    async canActivate(route: ActivatedRouteSnapshot, state: RouterStateSnapshot): Promise<boolean> {
        
        const that = this;
        function getData(): Promise<boolean>
        {
            return new Promise((resolve, reject) => {
                that.basketService.getProducts().subscribe(e => {

                    if(e.hasOwnProperty("userLog") && !e["userLog"]){
                        that.router.navigate([""]);
                        return reject(false)
                    }
                    else{
                        return resolve(true)
                    }
                    
                });
            });
        };

       const res = await getData();
       return res;
    }
}
