import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

import { DetailsOfProductDto } from 'src/app/dto/detailsOfProduct.dto';
import { CurrDataService } from '../curr-data/curr-data.service';

@Component({
  selector: 'app-detail',
  templateUrl: './detail.component.html',
  styleUrls: ['./detail.component.css']
})
export class DetailComponent implements OnInit {

  product: DetailsOfProductDto;

  constructor(
    private activatedRoute: ActivatedRoute,
    private currData: CurrDataService
  ){};

  ngOnInit(): void {
    this.activatedRoute.paramMap.subscribe(e => {
      const productID = parseInt(e.get("id"));

      this.currData.takeDetailsOfProduct(productID).subscribe((e: DetailsOfProductDto) => {
        this.product = e;
        let specyfication = this.product.stuffDetails.specification;

        if(specyfication !== null)
        {
          specyfication = JSON.parse(specyfication);
        }
        this.product.stuffDetails.specification = specyfication;
      });
    });
  };

  issetSpecification()
  {
    return this.product?.stuffDetails.specification !== null && this.product?.stuffDetails.specification.length > 0;
  }

}
