import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ProductDto } from '../dto/product.dto';
import { CurrDataService } from './curr-data.service';
import { FlowsProductsService } from './flows-products.service';
import { host } from './host';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.css']
})
export class MainComponent implements OnInit {

  urlSeason: string;
  urlGroup: string;

  constructor(
    private httpClient: HttpClient,
    private currDataServ: CurrDataService,
    private flowsProcuts: FlowsProductsService,
    private activatedRoute: ActivatedRoute
  ){}

  async ngOnInit()
  {
    let season: string;
    let group: string;

    this.activatedRoute.paramMap.subscribe(e => {
      season = e.get("period");
      group = e.get("group");

      this.urlSeason = season;
      this.urlGroup = group;
    })

    this.currDataServ.takeData(season, group).subscribe((e: ProductDto[]) => {
      this.flowsProcuts.products.next(e);
      console.log(e);
    });
  }
}
