styles:
*{
    box-sizing: border-box;
}
.double-slider{
  background-color:white;
    border:1px solid #DDDDDD;
    /* height: 10px; */
    height: 12px;
    /* width:100%; */
    position: relative;
    border-radius: 2px;
    max-width: 600px;
    margin:10px;
    cursor: pointer;
}
.disable{
    cursor: no-drop ;
    opacity: 0.6;
}
.slider{
    /* background-image: url("pepsi.png"); */
    box-shadow: 0 2px 2px rgba(0, 0, 0, 0.24), 0 0 2px rgba(0, 0, 0, 0.12);
   /* opacity: 0.4; */
    top: -12px;
    z-index: 3;
    width: 32px;
    height: 32px;
    cursor: inherit;
    background-color: #0db9f0;
    position: absolute;
display: inline-block;
white-space: nowrap;
border-radius: 16px;
transform-origin: 0 0;
will-change: transform;
}
.slider2{

    /* opacity: 0.4; */
     top: -12px;
     z-index: 3;
     width: 32px;
     height: 32px;
     cursor: pointer;
     background-color: #0db9f0;
     position: absolute;
 display: inline-block;
 white-space: nowrap;
 border-radius: 2px;
 transform-origin: 0 0;
 will-change: transform;
 }
.slider2::before{
    content: "";
    display: block;
    position: absolute;
    height: 14px;
    width: 1px;
    background: #E8E7E6;
    left: 14px;
    top: 6px;
}
.slider2::after{
    content: "";
    display: block;
    position: absolute;
    height: 14px;
    width: 1px;
    background: #E8E7E6;
    left: 14px;
    top: 6px;
}
.slider2:nth-child(1){
    z-index:4;
    left:0px;
    }
.slider2:nth-child(2){
    z-index:5;
/* right:-16px; */
right: 0px;
}
.slider:nth-child(1){
    left:0px;
    }
.slider:nth-child(2){
/* right:-16px; */
right: 0px;
}

.slider-progress{
    position: absolute;
    top:0px;
    /* width:calc(100% - 32px); */
   width:100%;
height: 100%;
    /* left:15px; */
left: 0px;
    /* height:10px; */
    height: inherit;
    background-color: #e5e5e5;  
    transform-origin: 0 0;
    will-change: transform;
}
.slider-disable{
    opacity: .35 !important;
    cursor: not-allowed !important;
}
.tooltip{
    position: absolute;
    top: -28px;
    left: -3px;
    z-index: 100;
    background-color: white;
    border: 1px solid dotted black;
    border-style: dotted;
    border: 2px solid lightseagreen;
    border-radius: 5px;
    padding: 2px;
    text-align: center;
    box-shadow: 0 2px 2px rgba(0, 0, 0, 0.24), 0 0 2px rgba(0, 0, 0, 0.12);    
}

.slider:active{
    box-shadow: 0 2px 2px rgba(0, 0, 0, 0.24), 0 0 2px rgba(0, 0, 0, 0.12);
}
.slider2 .tooltip{
    top:-17px;
    left:-5px;
}
.p-slider{
    display: inline-block;
    position: relative;
    box-sizing: border-box;
    padding: 8px;
    outline: 0;
    vertical-align: middle;
    height: 48px;
    min-width: 128px;
    width: 300px;
}
